script:
frappe.ui.form.on('User', {
    refresh: function(frm) {
        frm.toggle_display('last_name', false); // 隐藏字段
        frm.toggle_display('full_name', false); // 隐藏字段
        frm.toggle_display('middle_name', false); // 隐藏字段
        frm.toggle_display('username', false); // 隐藏字段
        //frm.toggle_display('field_name', true);  // 显示字段
    }
});

frappe.ui.form.on('Bank Account', {
    // 当自定义字段 custom_branch_code_autofetch 的值改变时
    custom_branch_code_autofetch: function (frm) {
        // 获取 custom_branch_code_autofetch 的值
        let branchCode = frm.doc.custom_branch_code_autofetch;

        // 将值赋给 branch_code 字段
        frm.set_value('branch_code', branchCode);
    },

    // 在表单加载时隐藏 branch_code 字段
    onload: function (frm) {
        frm.toggle_display('branch_code', false);
    }
});

frappe.ui.form.on('Bank', {
    refresh: function(frm) {
        frm.add_custom_button(__('View Branches'), function() {
            frappe.route_options = {
                'parent': frm.doc.name
            };
            frappe.set_route('List', 'Bank Branch', { 'parent': frm.doc.name });
        });
    }
});

frappe.ui.form.on('Address', {
    refresh: function(frm) {
        // 隐藏 county 字段
        frm.toggle_display('county', false);
    }
});
